mapper.select:
SELECT r.userId, r.spaceId,r.reservationDate, r.startTime , r.endTime, r.purpose, r.memo, r.regdate,s.name as spaceName, u.name as person, u.email as email, u.phone as phone FROM hsca219.reservationInfo as r
LEFT OUTER JOIN hsca219.user as u ON r.userId = u.id RIGHT OUTER JOIN hsca219.space as s ON r.spaceId = s.id
WHERE s.name = #{spaceName}

mapper.insert:
INSERT INTO hguclub.reservationInfo (userId, spaceId, startTime, endTime, purpose, memo, reservationDate) 
SELECT #{userId}, #{spaceId}, #{startTime}, #{endTime}, #{purpose},#{memo},#{reservationDate}
FROM dual WHERE NOT EXISTS(SELECT * FROM hguclub.reservationInfo WHERE spaceId = #{spaceId} 
AND reservationDate = #{reservationDate} AND startTime = #{startTime})

mapper.update:
UPDATE hguclub.reservationInfo set userId = #{userId},
spaceId=#{spaceId},startTime=#{startTime} ,
endTime=#{endTime},purpose=#{purpose},reservationDate=#{reservationDate}
WHERE id = #{id}
